    <script>
        //Exchange Drop Down Initiation
        function exchangeDropdown(that) {
            var rawPrice = jQuery(that).val();
            var prices = JSON.parse(rawPrice);
            var ele = '';
            var tabID = jQuery(that).parent().parent().attr('id');
            for (var key in prices) {
                if ( prices.hasOwnProperty(key) ) {
                    ele = prices[key];
                    jQuery('#'+tabID+' #table-price-' + key).text(ele);
                }
            };
        };


        jQuery(function($){
//Events Mobile Calendar Slide Up
            jQuery(".tribe-events-meta-group.tribe-events-meta-group-organizer").insertBefore(".tribe-events-single-event-description.tribe-events-content .et_pb_row.et_pb_row_1");

            jQuery('.sticky-ico-02').click(function(e) {
                e.preventDefault();
                jQuery('.tribe-events-cal-links').fadeToggle();
                jQuery('#sticky-events-mobile').toggleClass('active');

            });

            jQuery('.tribe-events-calendar-list__event-venue-address').each(function( index ) {
                if	(jQuery.trim(jQuery(this).text()) != "") {
                    jQuery(this).prepend(',');
                }
            });
            jQuery( ".ect-venue-details" ).each(function( index ) {

                var strVal = jQuery.trim(jQuery(this).text());
                var lastChar = strVal.slice(-1);
                if (lastChar == ',') { // check last character is string
                    jQuery(this).text(function (_,txt) {
                        return txt.replace(/,\s*$/, "");
                    });
                } else {
                    var text = jQuery(this).find('a');
                    var location = jQuery(this).find('.tribe-locality').text();
                    text.html(location);
                }
            });

            jQuery('.ect-load-more-btn').click(function(e) {
                setTimeout(function(){
                    jQuery( ".ect-venue-details" ).each(function( index ) {

                        var strVal = jQuery.trim(jQuery(this).text());
                        var lastChar = strVal.slice(-1);
                        if (lastChar == ',') { // check last character is string
                            jQuery(this).text(function (_,txt) {
                                return txt.replace(/,\s*$/, "");
                            });
                        } else {
                            var text = jQuery(this).find('a');
                            var location = jQuery(this).find('.tribe-locality').text();
                            text.html(location);
                        }
                    });
                },700);

            });



            jQuery( ".tribe-events-event-categories-label" ).each(function( index ) {
                var strVal = jQuery.trim(jQuery(this).text());
                var lastChar = strVal.slice(-1);
                if (lastChar == ':') {
                    jQuery(this).text(function (_,txt) {
                        return txt.replace(/:\s*$/, "");
                    });
                }
            });

            //Legal Accordion

            jQuery('.et_pb_accordion.legal .et_pb_toggle_open').addClass('et_pb_toggle_close').removeClass('et_pb_toggle_open');

            jQuery('.et_pb_accordion .et_pb_toggle').click(function() {
                $this = $(this);
                setTimeout(function(){
                    $this.closest('.et_pb_accordion').removeClass('et_pb_accordion_toggling');
                },700);
            });

            jQuery('.faq-anchor li:first-child').addClass('active');
            jQuery('.faq-anchor li a').click(function() {
                jQuery('.faq-anchor li').removeClass('active');
                jQuery(this).parent('li').addClass('active');
            });

            jQuery(".menu-item-41275 > a").addClass("fonoloModal").attr("data-toggle","modal");
            jQuery(".fonoloModal").click(function() {
                jQuery('#fonoloModal').modal('show');
            });
            /*
            jQuery( "article .post-content-inner p" ).each(function( index ) {
              jQuery(this).html(jQuery(this).text().substr(0,jQuery(this).text().indexOf(' ')));
            });*/

            jQuery("#dropdownMenuButton").click(function() {
                jQuery("#fonolo_call_schedule_time_items").toggle();
            });

            jQuery("#fonolo_call_schedule_time_items").click(function() {
                jQuery(this).toggle();
            });

            if (jQuery(window).width() <= 850) {

                jQuery("#et_search_icon").click(function() {
                    jQuery("#top-header").fadeOut(1000);
                });

                jQuery(".et_close_search_field").click(function() {
                    jQuery("#top-header").fadeIn(1000);
                });

            }


            jQuery('.select-membership').on('change', function(){
                var that = this;
                exchangeDropdown(that);
            });

            /* SITE MAP */




        jQuery(document).ready(function(jQuery) {

            jQuery('.feedzy-title').parent().parent().addClass('feedzy-text-module');

            jQuery( ".post-menu-list li" ).each(function( index ) {
                if (jQuery(this).html() === jQuery("meta[property='article:section']").attr("content")){
                    jQuery(this).addClass('active');
                }

            });

            var topMenu = jQuery(".sticky-post-menu-list ul"),
                navMenu = jQuery("#main-header"),
                topNav = jQuery("#top-header"),
                offset = 30,
                topMenuHeight = navMenu.outerHeight()+topNav.outerHeight()+offset,
                // All list items
                menuItems =  topMenu.find('a[href*="#"]'),
                // Anchors corresponding to menu items
                scrollItems = menuItems.map(function(){
                    var href = jQuery(this).attr("href"),
                        id = href.substring(href.indexOf('#')),
                        item = jQuery(id);
                    //console.log(item)
                    if (item.length) { return item; }
                });

            // so we can get a fancy scroll animation
            menuItems.click(function(e){
                var href = jQuery(this).attr("href"),
                    id = href.substring(href.indexOf('#'));
